import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';
import LoginForm from './components/LoginForm';
const Http = new XMLHttpRequest();

/*Steven Barker*/

var loggedIn = false;



async function middleMan(details){
  console.log(details);
  var usersname = await check(details).then((result) => {
    App.Login(details.email, usersname);
  });
}

function App() {

  async function check(details){
    console.log('2');
    Http.open("GET", `http://localhost:3500/login/${details.email}/${details.password}`);
    Http.send();
    var toReturn = null;
    console.log('3');
    Http.onreadystatechange = function (e) {
      if (this.readyState == 4 && this.status == 200) {
        console.log('4');
        console.log(Http.responseText);
        console.log('5');
        return(Http.responseText);
      }

    }

  }
  const adminUser = {
    email: "admin@admin.com",
    password: "password"
  }
  const [user, setUser] = useState({name: "", email:""});
  const [error, setError] = useState("");

  const Login = async(email, usersname) => {

    //console.log(check(details.email,details.password));
   // console.log('1');
    //var usersname = await check(details)
   // console.log('6');
    if(usersname != null){
      //console.log('7');
        //console.log(`name: ${usersname}`);
        console.log("logged in");
        setUser({
          email: email,
          name: usersname
        });
        loggedIn = true;
      }else{
        console.log("failure");
        setError("Details do not match");
      }
  }

  const Logout = () => {
    console.log("Logout");
    loggedIn = false
  }


  return (
    <div className="App">
      {(loggedIn) ? (
        <div className="welcome">
          <h2>Welcome, <span>{user.name}</span></h2>
          <button onClick= {Logout}>Logout</button>
        </div>  
      ): ( 
        <LoginForm Login={Login} error={error}/>
      )}
    </div>
  ) 
}

export default App;
